// Custom button with advanced interaction
function CustomButton(props) {
  const handleClick = () => {
    // Custom logic here
    console.log('Button clicked!');
    props.onCustomAction();
  };

  return (
    <button 
      className="custom-btn"
      onClick={handleClick}
    >
      {props.label}
    </button>
  );
}
